// app/teleprompter/page.js

'use client';
import { useState, useEffect, useRef } from 'react';
import { io } from 'socket.io-client';

export default function TeleprompterPage() {
  const [scriptContent, setScriptContent] = useState('');
  const socketRef = useRef(null);
  const wakeLockRef = useRef(null);
  const videoRef = useRef(null);

  // Function to request wake lock
  const keepScreenAwake = async () => {
    try {
      // Method 1: Wake Lock API
      if ('wakeLock' in navigator) {
        wakeLockRef.current = await navigator.wakeLock.request('screen');
        console.log('Wake Lock is active');
      }
    } catch (err) {
      console.error(`Wake lock failed: ${err.message}`);
    }

    try {
      // Method 2: Using video playback
      if (videoRef.current) {
        videoRef.current.play();
      }
    } catch (err) {
      console.error(`Video playback failed: ${err.message}`);
    }
  };

  useEffect(() => {
    // Initialize socket connection
    fetch("/api/socket").then(() => {
      socketRef.current = io({
        path: "/api/socketio",
      });
      
      // Listen for script updates
      socketRef.current.on('scriptUpdate', (data) => {
        setScriptContent(data.content);
      });
    });

    // Keep screen awake when component mounts
    keepScreenAwake();

    // Re-request wake lock if page becomes visible again
    const handleVisibilityChange = () => {
      if (document.visibilityState === 'visible') {
        keepScreenAwake();
      }
    };

    // Periodically refresh wake lock
    const wakeLockInterval = setInterval(keepScreenAwake, 30000);

    document.addEventListener('visibilitychange', handleVisibilityChange);
    document.addEventListener('click', keepScreenAwake);
    document.addEventListener('touchstart', keepScreenAwake);

    // Cleanup on unmount
    return () => {
      if (socketRef.current) {
        socketRef.current.disconnect();
      }
      if (wakeLockRef.current) {
        wakeLockRef.current.release();
      }
      clearInterval(wakeLockInterval);
      document.removeEventListener('visibilitychange', handleVisibilityChange);
      document.removeEventListener('click', keepScreenAwake);
      document.removeEventListener('touchstart', keepScreenAwake);
    };
  }, []);

  return (
    <div 
      className="min-h-screen bg-black"
      style={{
        height: '100vh',
        overflow: 'hidden'
      }}
    >
      {/* Hidden video element to help keep screen awake */}
      <video 
        ref={videoRef}
        playsInline
        muted
        loop
        style={{ 
          width: '1px',
          height: '1px',
          position: 'absolute',
          top: '-1px',
          left: '-1px',
          opacity: 0
        }}
      >
        <source src="data:video/mp4;base64," type="video/mp4" />
      </video>

      <div 
        className="h-full flex items-center justify-center px-8"
      >
        <div 
          className="text-white text-6xl text-center"
          style={{
            transform: 'scaleX(-1)', // Mirror the text horizontally
          }}
        >
          {scriptContent || 'Ready'}
        </div>
      </div>
    </div>
  );
}
